INSERT INTO OBD_ETP_ETAPAS_PRG (ETP_CODIGO, ETP_CODGRC, ETP_NOMBRE, ETP_DESCRIPCION, ETP_ORDEN, ETP_ANTES_PRIMER_DIA, ETP_DURANTE_PRIMER_DIA, ETP_POSTERIOR_PRIMER_DIA, ETP_PERIODO_PRUEBA) VALUES (1, 1, 'Antes del Primer Día de Trabajo', 'Actividades previas al inicio oficial del empleo, como la configuración de la cuenta de correo, entrega de documentación, y asignación de equipo.', 1, 1, 0, 0, 0);
INSERT INTO OBD_ETP_ETAPAS_PRG (ETP_CODIGO, ETP_CODGRC, ETP_NOMBRE, ETP_DESCRIPCION, ETP_ORDEN, ETP_ANTES_PRIMER_DIA, ETP_DURANTE_PRIMER_DIA, ETP_POSTERIOR_PRIMER_DIA, ETP_PERIODO_PRUEBA) VALUES (2, 1, 'Primer Día de Trabajo', 'Actividades del primer día de trabajo, como la bienvenida por parte del equipo, revisión de políticas de la empresa y recorrido por las instalaciones.', 2, 0, 1, 0, 0);
INSERT INTO OBD_ETP_ETAPAS_PRG (ETP_CODIGO, ETP_CODGRC, ETP_NOMBRE, ETP_DESCRIPCION, ETP_ORDEN, ETP_ANTES_PRIMER_DIA, ETP_DURANTE_PRIMER_DIA, ETP_POSTERIOR_PRIMER_DIA, ETP_PERIODO_PRUEBA) VALUES (3, 1, 'Primera Semana de Trabajo', 'Actividades durante la primera semana, como la capacitación inicial, integración con el equipo y asignación de tareas iniciales.', 3, 0, 0, 1, 1);
INSERT INTO OBD_ETP_ETAPAS_PRG (ETP_CODIGO, ETP_CODGRC, ETP_NOMBRE, ETP_DESCRIPCION, ETP_ORDEN, ETP_ANTES_PRIMER_DIA, ETP_DURANTE_PRIMER_DIA, ETP_POSTERIOR_PRIMER_DIA, ETP_PERIODO_PRUEBA) VALUES (4, 1, 'Primeros Tres Meses de Trabajo', 'Actividades durante los primeros tres meses, incluyendo evaluaciones de desempeño y reuniones de seguimiento para medir el progreso del empleado.', 4, 0, 0, 1, 1);
INSERT INTO OBD_ETP_ETAPAS_PRG (ETP_CODIGO, ETP_CODGRC, ETP_NOMBRE, ETP_DESCRIPCION, ETP_ORDEN, ETP_ANTES_PRIMER_DIA, ETP_DURANTE_PRIMER_DIA, ETP_POSTERIOR_PRIMER_DIA, ETP_PERIODO_PRUEBA) VALUES (5, 1, 'Primer Año de Trabajo', 'Actividades relacionadas con el seguimiento y evaluación a lo largo del primer año de trabajo, como las revisiones anuales de desempeño y ajustes en las metas laborales.', 5, 0, 0, 1, 0);
