INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (1, 2, 'Configuración del Entorno de Desarrollo', 'Esta actividad implica la configuración del entorno de desarrollo para que el empleado pueda comenzar a trabajar. Incluye la instalación de IDEs, configuración de repositorios de código y acceso a las herramientas internas.', 'Asegurar que el empleado tiene acceso y puede operar eficientemente en el entorno de desarrollo del equipo.', 2, 12, 0, 'Dias', 2, 'Dias', 1, 1, NULL, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (2, 2, 'Introducción a las Metodologías Ágiles', 'Se presenta al empleado las prácticas y procedimientos que sigue el equipo de desarrollo, basados en metodologías ágiles como Scrum o Kanban. Incluye la asistencia a una sesión de formación y la participación en una ceremonia ágil.', 'Familiarizar al empleado con los principios ágiles y su aplicación en el equipo.', 3, 2, 3, 'Dias', 1, 'Dias', 2, 2, 3, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (3, 2, 'Introducción a los Proyectos Actuales', 'Se le brinda al nuevo empleado una visión general de los proyectos en los que está involucrado el equipo de desarrollo, detallando los objetivos, estado actual y próximos hitos.', 'Proporcionar al empleado el contexto necesario sobre los proyectos actuales del equipo para que pueda comenzar a contribuir.', 4, 13, 5, 'Dias', 2, 'Dias', 1, 3, 4, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (4, 3, 'Introducción a la Visión Estratégica de la Empresa', 'Presentación sobre la estrategia a largo plazo de la empresa, sus valores y objetivos clave.', 'Alinear al nuevo gerente con la visión y objetivos estratégicos de la empresa.', 2, 1, 0, 'Dias', 2, 'Horas', 1, 1, 1, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (5, 3, 'Taller de Liderazgo Regional', 'Capacitación en liderazgo para la gestión de equipos regionales y manejo de la diversidad cultural.', 'Desarrollar habilidades de liderazgo y comunicación efectiva en un contexto regional.', 3, 11, 1, 'Dias', 1, 'Dias', 2, 2, 1, 4, 80.00);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (6, 3, 'Curso de Planificación Financiera Regional', 'Entrenamiento en la elaboración de presupuestos y análisis financiero regional.', 'Equipar al nuevo gerente con las habilidades necesarias para gestionar la planificación financiera en su región.', 3, 2, 2, 'Dias', 3, 'Dias', 3, 3, NULL, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (7, 3, 'Orientación en el Uso de las Tecnologías de la Información', 'Guía sobre las herramientas tecnológicas utilizadas en la compañía, desde sistemas de gestión hasta comunicación interna.', 'Familiarizar al gerente con las tecnologías de la empresa para su uso diario.', 4, 2, 6, 'Dias', 1, 'Dias', 2, 4, 1, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (8, 3, 'Evaluación del Desempeño Regional', 'Revisión y análisis de indicadores de desempeño en su región.', 'Monitorear y mejorar el desempeño en función de los KPIs regionales.', 5, 5, 90, 'Dias', 2, 'Dias', 2, 5, 4, 1, 70.00);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (9, 7, 'Introducción a Políticas y Procedimientos Administrativos', 'Presentación de las políticas internas, procedimientos administrativos, y normativas que deben ser seguidas por el personal administrativo en sus funciones diarias.', 'Familiarizar al nuevo empleado con las normativas y procedimientos administrativos que rigen su desempeño en la organización.', 2, 3, 0, 'Dias', 2, 'Dias', 1, 1, 1, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (10, 7, 'Capacitación en Herramientas de Gestión Administrativa', 'Capacitación en el uso de software y herramientas digitales necesarias para la gestión de tareas administrativas, incluyendo sistemas de gestión de archivos y herramientas de productividad.', 'Asegurar que el nuevo empleado esté capacitado para utilizar las herramientas digitales de la organización de manera eficiente.', 3, 2, 2, 'Dias', 3, 'Dias', 2, 2, 1, NULL, NULL);
