INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (12, 8, 'Formación en Políticas de Recursos Humanos', 'Capacitación sobre las políticas de recursos humanos, beneficios, compensaciones y obligaciones del empleado.', 'Asegurar que el empleado comprenda los procedimientos y beneficios de la empresa.', 3, 9, 1, 'Dias', 1, 'Dias', 2, 3, 1, NULL, NULL);
INSERT INTO OBD_PAC_PLANT_ACTIVIDADES (PAC_CODIGO, PAC_CODPPR, PAC_NOMBRE, PAC_DESCRIPCION, PAC_OBJETIVO, PAC_CODETP, PAC_CODTAC, PAC_OFFSET_INICIO, PAC_UNIDAD_OFFSET_INICIO, PAC_DURACION_ESTIMADA, PAC_UNIDAD_DURACION, PAC_CODPRI, PAC_ORDEN, PAC_CODTRA, PAC_CODTEV, PAC_NOTA_EVAL_ESPERADA) VALUES (13, 8, 'Configuración de Accesos y Herramientas de Trabajo', 'Configuración de los accesos a sistemas internos, correo electrónico y otras herramientas necesarias para el trabajo.', 'Garantizar que el empleado tenga acceso a todas las plataformas necesarias para realizar su trabajo.', 3, 12, 2, 'Dias', 4, 'Dias', 1, 4, 1, NULL, NULL);
