INSERT INTO OBD_PPR_PLANTILLA_PRG (PPR_CODIGO, PPR_CODGRC, PPR_NOMBRE, PPR_DESCRIPCION, PPR_OBJETIVO, PPR_ESTADO, PPR_DURACION_ESTIMADA, PPR_UNIDAD_DURACION) VALUES (2, 1, 'Onboarding para Personal de Desarrollo', 'Programa diseñado para la integración de nuevos empleados en los equipos de desarrollo y soporte tecnológico', 'Asegurar que los empleados nuevos comprendan las herramientas, frameworks y procesos tecnológicos, y se adapten a la cultura corporativa de innovación.', 'Activo', 30, 'Dias');
INSERT INTO OBD_PPR_PLANTILLA_PRG (PPR_CODIGO, PPR_CODGRC, PPR_NOMBRE, PPR_DESCRIPCION, PPR_OBJETIVO, PPR_ESTADO, PPR_DURACION_ESTIMADA, PPR_UNIDAD_DURACION) VALUES (3, 1, 'Onboarding Gerencial Regional', 'Programa enfocado en la inducción de nuevos gerentes que supervisarán las operaciones en diferentes regiones de la empresa.', 'Capacitar a los nuevos gerentes en liderazgo, gestión de equipos y toma de decisiones estratégicas, con un enfoque en las particularidades de cada región.', 'Activo', 60, 'Dias');
INSERT INTO OBD_PPR_PLANTILLA_PRG (PPR_CODIGO, PPR_CODGRC, PPR_NOMBRE, PPR_DESCRIPCION, PPR_OBJETIVO, PPR_ESTADO, PPR_DURACION_ESTIMADA, PPR_UNIDAD_DURACION) VALUES (6, 1, 'Onboarding Manufactura', 'Programa diseñado para introducir a los empleados de las plantas de manufactura a los procesos productivos, normas de seguridad y estándares de calidad.', 'Capacitar a los nuevos empleados en las normas de seguridad, los procedimientos operativos y la cultura de calidad de la planta de manufactura.', 'Inactivo', 15, 'Dias');
INSERT INTO OBD_PPR_PLANTILLA_PRG (PPR_CODIGO, PPR_CODGRC, PPR_NOMBRE, PPR_DESCRIPCION, PPR_OBJETIVO, PPR_ESTADO, PPR_DURACION_ESTIMADA, PPR_UNIDAD_DURACION) VALUES (7, 1, 'Onboarding para Personal Administrativo', 'Programa de onboarding para empleados en roles administrativos, incluyendo orientación en sistemas de gestión internos y procedimientos administrativos.', 'Integrar al personal administrativo de manera que puedan apoyar eficazmente las operaciones diarias de la empresa.', 'Activo', 20, 'Dias');
INSERT INTO OBD_PPR_PLANTILLA_PRG (PPR_CODIGO, PPR_CODGRC, PPR_NOMBRE, PPR_DESCRIPCION, PPR_OBJETIVO, PPR_ESTADO, PPR_DURACION_ESTIMADA, PPR_UNIDAD_DURACION) VALUES (8, 1, 'Onboarding General para Nuevos Empleados', 'Programa estándar de onboarding para integrar a los nuevos empleados a la organización, cubriendo aspectos generales de la empresa y su cultura.', 'Facilitar la rápida integración de nuevos empleados a la empresa y alinearlos con la cultura organizacional.', 'Activo', 30, 'Dias');
INSERT INTO OBD_PPR_PLANTILLA_PRG (PPR_CODIGO, PPR_CODGRC, PPR_NOMBRE, PPR_DESCRIPCION, PPR_OBJETIVO, PPR_ESTADO, PPR_DURACION_ESTIMADA, PPR_UNIDAD_DURACION) VALUES (9, 1, 'Onboarding Call Center', 'Programa de inducción para los nuevos empleados que se integran al área de Call Center, enfocado en atención al cliente, manejo de llamadas y resolución de problemas.', 'Asegurar que los nuevos colaboradores comprendan los procesos de atención al cliente, los scripts de llamadas y los sistemas de gestión de interacción con clientes.', 'Activo', 20, 'Dias');
