INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (1, 1, 'Inducción General a la Empresa', 'Introducción a la empresa, su cultura, misión, visión y valores.', 0, 0, 0, 0, 1, 2, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (2, 1, 'Capacitación Técnica', 'Entrenamiento en las herramientas(software, plataformas internas) y procesos técnicos necesarios para el rol.', 0, 1, 1, 1, 1, 1, 'Semanas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (3, 1, 'Revisión de Políticas', 'Revisión de las políticas internas de la empresa y cumplimiento normativo.', 0, 1, 0, 1, 0, 1, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (4, 1, 'Asignación de Mentor', 'Asignación de un mentor para guiar al nuevo empleado durante el primer mes.', 1, 0, 1, 0, 1, 3, 'Meses');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (5, 1, 'Evaluación Inicial de Desempeño', 'Evaluación de desempeño y ajuste de expectativas después del primer mes.', 0, 1, 1, 0, 1, 1, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (6, 1, 'Presentación de Equipos de Trabajo', 'Reunión para presentar al nuevo empleado con su equipo inmediato.', 0, 0, 0, 0, 1, 1, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (7, 1, 'Firma de Documentación Legal', 'Firma de contratos, acuerdos de confidencialidad y otros documentos legales.', 0, 1, 0, 1, 1, 1, 'Dias');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (8, 1, 'Recorrido por las Instalaciones', 'Tour por las instalaciones de la empresa para familiarizar al empleado con su entorno físico.', 0, 0, 0, 0, 1, 1, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (9, 1, 'Asesoría en Beneficios y Prestaciones', 'Orientación sobre los beneficios y prestaciones ofrecidos por la empresa, como seguros, vacaciones, etc.', 0, 0, 0, 1, 1, 1, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (10, 1, 'Programa de Capacitación Técnica Avanzada', 'Entrenamiento intensivo en el uso de software o maquinaria especializada, necesario para desempeñar las funciones técnicas del cargo.', 1, 1, 1, 1, 1, 2, 'Meses');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (11, 1, 'Desarrollo de Habilidades de Liderazgo', 'Programa orientado a preparar al empleado para roles de liderazgo, mediante talleres, simulaciones y proyectos reales de gestión de equipos.', 1, 1, 1, 1, 1, 5, 'Meses');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (12, 1, 'Configuración de Herramientas de Trabajo', 'Enfocado en asegurar que el nuevo empleado tenga todas las herramientas y recursos necesarios para desempeñar sus funciones. Puede ser realizada por un empleado interno o por el nuevo empleado siguiendo una guía o capacitación específica.', 0, 1, 0, 0, 0, 5, 'Horas');
INSERT INTO OBD_TAC_TIPOS_ACTIVIDAD (TAC_CODIGO, TAC_CODGRC, TAC_NOMBRE, TAC_DESCRIPCION, TAC_REQ_NOTIFICACION, TAC_REQ_RECORDATORIO, TAC_REQ_EVALUACION, TAC_REQ_DOCUMENTOS, TAC_REQ_RESPONSABLE, TAC_DURACION_ESTIMADA, TAC_UNIDAD_DURACION) VALUES (13, 1, 'Introducción a los Proyectos de la Empresa', 'Proporciona al nuevo empleado una visión general de los proyectos actuales y futuros en los que está trabajando la empresa. Esto incluye objetivos, plazos, equipos involucrados y su rol específico dentro de estos proyectos.', 1, 1, 0, 0, 0, 2, 'Horas');
